function getNameVehicle(idModel, categoria)
    if categoria == "Automobile" then
        for i, v in pairs(config['Veiculos']['Carros']) do
            if config['Veiculos']['Carros'][i][2] == idModel then
                return config['Veiculos']['Carros'][i][1]
            end
        end
    end
    if categoria == "Bike" or categoria == "BMX" or categoria == "Quad" then
        for i, v in pairs(config['Veiculos']['Motos']) do
            if config['Veiculos']['Motos'][i][2] == idModel then
                return config['Veiculos']['Motos'][i][1]
            end
        end
    end
    if categoria == "Helicopter" then
        for i, v in pairs(config['Veiculos']['Helicopteros']) do
            if config['Veiculos']['Helicopteros'][i][2] == idModel then
                return config['Veiculos']['Helicopteros'][i][1]
            end
        end
    end
 if categoria == "Boat" then
        for i, v in pairs(config['Veiculos']['Barcos']) do
            if config['Veiculos']['Barcos'][i][2] == idModel then
                return config['Veiculos']['Barcos'][i][1]
            end
        end
    end
end

formatNumber = function(number)   
    local formatted = number   
    while true do       
        formatted, k = string.gsub(formatted, "^(-?%d+)(%d%d%d)", '%1.%2')     
        if ( k==0 ) then       
            break   
        end   
    end   
    return formatted 
end 

notifyS = function(player, message, type)
   exports['fipi_infobox']:addBox(player, message, type)
end

notifyC = function(message, type)
exports['fipi_infobox']:addBox(player, message, type)
end

function removeHex(message)
	if (type(message) == "string") then
		while (message ~= message:gsub("#%x%x%x%x%x%x", "")) do
			message = message:gsub("#%x%x%x%x%x%x", "")
		end
	end
	return message or false
end

function puxarNome(player)
    return removeHex(getPlayerName(player))
end

function puxarID(player)
    return (getElementData(player, "ID") or 0)
end

function puxarConta(player)
    return getAccountName(getPlayerAccount(player))
end

